import { Component, OnInit } from '@angular/core';
import { Pagamento } from 'src/app/models/pagamento';
import { PagamentoService } from 'src/app/services/pagamento.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  public pagamentos!: Pagamento[];
  public colunas: string[] = ['descricao', 'valorPagamento', 'tipoPagamento', 'dataPagamento'];

  constructor(private pagamentoService: PagamentoService) {}

  ngOnInit(): void {

    this.pagamentoService.buscarPagamentos().subscribe((response: Pagamento[])=>{
      this.pagamentos = response;
    })
  }

}
<app-toolbar [titulo]="'Gohan'"></app-toolbar>

<div class="buscar-por-data">
  <input type="date">
  <button>Pesquisar</button>
</div>
<table mat-table [dataSource]="pagamentos" class="mat-elevation-z8">

  <!--- Note that these columns can be defined in any order.
        The actual rendered columns are set as a property on the row definition" -->

  <!-- Position Column -->
  <ng-container matColumnDef="descricao">
    <th mat-header-cell *matHeaderCellDef> Descricao </th>
    <td mat-cell *matCellDef="let element"> {{element.descricao}} </td>
  </ng-container>

  <!-- Name Column -->
  <ng-container matColumnDef="valorPagamento">
    <th mat-header-cell *matHeaderCellDef> Valor do pagamento </th>
    <td mat-cell *matCellDef="let element"> {{element.valorPagamento}} </td>
  </ng-container>

  <!-- Weight Column -->
  <ng-container matColumnDef="dataPagamento">
    <th mat-header-cell *matHeaderCellDef> Data do pagamento </th>
    <td mat-cell *matCellDef="let element"> {{element.dataPagamento}} </td>
  </ng-container>

  <!-- Symbol Column -->
  <ng-container matColumnDef="tipoPagamento">
    <th mat-header-cell *matHeaderCellDef> Método de pagamento </th>
    <td mat-cell *matCellDef="let element"> {{element.tipoPagamento == 'C' ? 'Crédito' : 'Débito'}} </td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="colunas" class="cols"></tr>
  <tr mat-row *matRowDef="let row; columns: colunas;"></tr>
</table>
