import { Component } from '@angular/core';
import Gestor from './models/Gestor';
import Analista from './models/Analista';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public analista: Analista[] = new Array<Analista>();
  public gestor: Gestor = new Gestor("Maria", 192076, "Gestora de TI", []);

  constructor(){
    this.analista.push(new Analista("Thompsom", 121314, "Analista de infraesturutra", this.gestor.nome))
    this.analista.push(new Analista("Goku", 4, "Lutador de malware", this.gestor.nome))


    this.gestor.nomeSubordinados = this.analista.map(analista => analista.nome);
  }

}
<div *ngFor="let item of analista">
    {{item.nome}}
</div>


<div>
  <div class="gestor">
    <span>Informações do gestor(a)</span>
    <p>Nome: {{ gestor.nome }}</p>
    <p>Cargo: {{ gestor.cargo }}</p>
    <p>Funcional: {{ gestor.funcional }}</p>

  </div>
  <div class = "subordinados">
      <p class="sub">Subordinados:</p>
    <div *ngFor="let item of list">

    </div>
  </div>
</div>
