import { Component } from '@angular/core';
import Gestor from './models/Gestor';
import Analista from './models/Analista';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  public analistas: Analista[] = new Array<Analista>();
  public gestor: Gestor = new Gestor("Maria", 192076, "Gestora de TI", []);

  constructor(){
    this.analistas.push(new Analista("Thompsom", 121314, "Analista de infraesturutra", this.gestor.nome))
    this.analistas.push(new Analista("Goku", 4, "Lutador de malware", this.gestor.nome))


    this.gestor.nomeSubordinados = this.analistas.map(analista => analista.nome);
  }

}


<div class = "gestor-wrapper">

  <div class="gestor">
    <span>Informações do gestor(a):</span>
    <p>Nome: {{ gestor.nome }}</p>
    <p>Cargo: {{ gestor.cargo }}</p>
    <p>Funcional: {{ gestor.funcional }}</p>

  </div>

  <div class = "subordinados">
      <p class="sub">Subordinados:</p>
    <div *ngFor="let subordinado of gestor.nomeSubordinados">
      <p>{{ subordinado }}</p>
    </div>
  </div>

</div>


<div class = "analistas-wrapper">
    <div class="analistas" *ngFor="let analista of analistas">

      <span>Informações de analistas</span>
      <p>Nome: {{ analista.nome }}</p>
      <p>Cargo: {{ analista.cargo }}</p>
      <p>Funcional: {{ analista.funcional }}</p>
      <p>Gestor: {{ analista.nomeGestor }}</p>

    </div>
</div>
