import { Component } from '@angular/core';
import { Anime } from 'src/app/model/anime';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  public animes: Anime[] = new Array<Anime>();

  constructor(){
    this.animes.push(new Anime(1, "Dragon Ball", 1986, 9))
    this.animes.push(new Anime(2, "Dragon Ball Kai", 2009, 5))
  }

}
<p>home works!</p>
<app-navbar></app-navbar>

<div *ngFor="let anime of animes">
  <div class="anime">
    <p>{{anime.nome}}</p>
    <p>{{anime.id}}</p>
    <p>{{anime.ano}}</p>
    <p>{{ anime.quantidadeDeTemporada }}</p>
  </div>
</div>
