import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Animal } from 'src/app/models/animal';
import { AnimalService } from 'src/app/services/animal.service';

@Component({
  selector: 'app-adicionar-animal',
  templateUrl: './adicionar-animal.component.html',
  styleUrls: ['./adicionar-animal.component.css']
})
export class AdicionarAnimalComponent {

  public nome!: string;
  public raca!: string;
  public peso!: number;


  constructor(private animalService: AnimalService, private router: Router){ }

  public adicionarAnimal(){
    let animal: Animal = new Animal(this.nome, this.raca, this.peso)
    this.animalService.adicionarAnimal(animal).subscribe((response) => {
      console.log(response)
    })
    this.router.navigate(["/"])
  }
}
<div class="formulario">
  <input type="text" placeholder="Nome do animal" [(ngModel)]="nome">
  <input type="text" placeholder="Raça" [(ngModel)]="raca">
  <input type="number" placeholder="Peso" [(ngModel)]="peso">
  <button (click)="adicionarAnimal()">Adicionar animal</button>
</div>
