import { Component, OnInit } from '@angular/core';
import { ProdutoService } from 'src/app/services/produto.service';
import { Produto } from 'src/app/model/produto';

@Component({
  selector: 'app-listagem-produto',
  templateUrl: './listagem-produto.component.html',
  styleUrls: ['./listagem-produto.component.css']
})
export class ListagemProdutoComponent implements OnInit{

  public produto: Produto[] = [];

  constructor(private produtoService: ProdutoService){ }

  ngOnInit(): void {
    this.produtoService.buscarProdutos().subscribe((response)=> {
      console.log("response: ")
      console.log(response)

      return this.produto = response

    })
  }


}

<div class*ngFor="let produtos of produto">
  <p>Produto: {{ produtos.nome }} </p>
  <p>Descrição: {{ produtos.descricao }}</p>
  <img src="{{ produtos.imagem }}" alt="{{ produtos.descricao }}">
  <p> Preço: {{ produtos.valor }}</p>
  <p>Categoria: {{ produtos.categoria }}</p>
</div>
