import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Produto } from 'src/app/model/produto';
import { ProdutoService } from 'src/app/services/produto.service';

@Component({
  selector: 'app-adicionar-produto',
  templateUrl: './adicionar-produto.component.html',
  styleUrls: ['./adicionar-produto.component.css']
})
export class AdicionarProdutoComponent {

  public nome!: string;
  public descricao!: string;
  public imagem!: string;
  public valor!: number;
  public categoria!: string;

  constructor(private produtoService: ProdutoService, private router: Router){ }

  public AdicionarProduto(){
    let produto: Produto = new Produto(this.nome, this.descricao, this.imagem, this.valor, this.categoria)
     this.produtoService.adicionarProdutos(produto).subscribe((response) => {
      console.log(response)
    })
    this.router.navigate(["/"])

  }



}
<div class="formulario">
  <input type="text" placeholder="Nome" [(ngModel)]="nome">
  <input type="text" placeholder="Descrição" [(ngModel)]="descricao">
  <input type="text" placeholder="Imagem" [(ngModel)]="imagem">
  <input type="number" placeholder="Valor" [(ngModel)]="valor">
  <input type="text" placeholder="Categoria" [(ngModel)]="categoria">
  <button (click)="AdicionarProduto()">Adicionar animal</button>
</div>
