import { Component, OnInit } from '@angular/core';
import { LojaService } from 'src/app/loja.service';
import { Produto } from 'src/app/model/Produto';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  public produtos: Produto[] = [];

  constructor(private lojaService: LojaService){ }


  ngOnInit(): void {
    this.lojaService.getProduto().subscribe((response) => {
      console.log(response)
      this.produtos = response;
    })
  }

}
<p>home works!</p>
<div *ngFor="let produto of produtos">
  <mat-card class="example-card">
    <mat-card-header>
      <mat-card-title-group>
        <mat-card-title>{{ produto.nome }}</mat-card-title>
        <mat-card-subtitle>{{ produto.preco }}</mat-card-subtitle>
        <mat-card-subtitle>{{ produto.estoque }}</mat-card-subtitle>
        <img mat-card-xl-image src="{{ produto.imagem }}">
      </mat-card-title-group>
    </mat-card-header>
    <mat-card-content>
      {{ produto.descricao }}
    </mat-card-content>
  </mat-card>
</div>
